import React from 'react';
import ReactDOM from 'react-dom';
let headStyle = {
    color: 'green',

}
let currDate = new Date();
let currHour = currDate.getHours();
let greet;
if (currHour >= 1 && currHour <= 11){
    greet = 'Good Morning';
    headStyle.color = 'green';
}
else if (currHour >= 12 && currHour <= 19){
    greet = 'Good Afternoon';
    headStyle.color = 'orange';

}
else if (currHour > 19){
    greet = 'Good Night';
    headStyle.color = 'darkblue';

}

ReactDOM.render(
    <>
    <h1 class = "h"> Hello, {greet} </h1>
    </>,
    document.getElementById('root')
);

