import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
let headStyle = { }
let currDate = new Date();
let currHour = currDate.getHours();
let greet;
if (currHour >= 1 && currHour <= 11){
    greet = "Good Morning";
    headStyle.color = 'green';
}
else if (currHour >= 12 && currHour <= 19){
    greet = " Good Afternoon";
    headStyle.color = 'orange';

}
else if (currHour > 19){
    greet = " Good Night";
    headStyle.color = 'darkblue';
}
ReactDOM.render(
    <>
        <div>
        <h2> Hello </h2>
            <h1 > 
                Hello, <span style = {headStyle}> {greet}</span> 
            </h1>
        </div>
    </>,
    document.getElementById('root')
);

